mapper.sql:
			FROM
		    code c
		left JOIN codegroup cg ON c.codeGroup_cgSeq = cg.cgSeq
		WHERE
		    1=1

		AND c.cdUse = #{shUseNy}
        AND c.cdDelNy = #{shDelNy}


            AND c.cdSeq = #{shValue}
            AND c.cdName LIKE CONCAT('%',#{shValue},'%')
            AND c.cdNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT
c.codeGroup_cgSeq, c.cdUse, cg.cgName, c.cdSeq, c.cdName, c.cdNameEng, c.cdOrder, c.cdRegDate, c.cdEdDate, c.cdCheck, c.cdDelNy
ORDER BY c.cdSeq DESC limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
INSERT INTO code ( cdUse, cdName, cdNameEng, cdOrder, codeGroup_cgSeq, cdRegDate, cdDelNy
) values ( #{cdUse}, #{cdName}, #{cdNameEng}, #{cdOrder}, #{codeGroup_cgSeq}, NOW(), 0 )
SELECT last_insert_id()

mapper.delete:
Update code set
	cdDelNy = 1
where cdSeq = #{cdSeq}
