mapper.select:
SELECT
c.codeGroup_cgSeq, c.cdDelNy, cg.cgName, c.cdSeq, c.cdName, c.cdNameEng, c.cdOrder, c.cdRegDate, c.cdEdDate, c.cdCheck
ORDER BY c.cdSeq DESC limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
INSERT INTO code ( cdDelNy, cdName, cdNameEng, cdOrder, codeGroup_cgSeq, cdRegDate
) values ( #{cdDelNy}, #{cdName}, #{cdNameEng}, #{cdOrder}, #{codeGroup_cgSeq}, NOW() )
SELECT last_insert_id()
