mapper.insert:
INSERT INTO gamelinescore (
glHomeRuns, glHomeHits, glHomeErrors, glHomeLob, glHomeWin, glAwayRuns, glAwayHits, glAwayErrors, glAwayLob, glAwayWin, glRegTime, glModTime, game_gmSeq
) VALUES ( #{glHomeRuns}, #{glHomeHits}, #{glHomeErrors}, #{glHomeLob}, #{glHomeWin}, #{glAwayRuns}, #{glAwayHits}, #{glAwayErrors}, #{glAwayLob}, #{glAwayWin}, #{glRegTime}, #{glModTime}, #{game_gmSeq} )

INSERT INTO gameInning (
giSeq, giInning, giHomeScore, giAwayScore, giRegTime, giModTime, game_gmSeq
) VALUES ( #{giSeq}, #{giInning}, #{giHomeScore}, #{giAwayScore}, #{giRegTime}, #{giModTime}, #{game_gmSeq} )
