mapper.sql:
		from
	  stadium s left join team tm ON s.team_tmSeq = tm.tmSeq
		WHERE 1=1

		AND s.stUse = #{shUseNy}
        AND s.stDelNy = #{shDelNy}


				AND s.stRegTime BETWEEN #{shDateStart} AND #{shDateEnd}
				AND s.stModTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND s.stSeq = #{shValue}
            AND s.stName LIKE CONCAT('%',#{shValue},'%')
            AND s.stNameKor LIKE CONCAT('%',#{shValue},'%')
            AND s.stAdress LIKE CONCAT('%',#{shValue},'%')


        AND tmSeq = #{shTeamOption}

mapper.insert:
INSERT INTO stadium ( stDelNy, stUse, stName, stNameKor, stAdress, team_tmSeq, stRegTime
) values ( 0, #{stUse}, #{stName}, #{stNameKor}, #{stAdress}, #{team_tmSeq}, NOW() )
SELECT last_insert_id()

mapper.delete:
Update stadium set
	stDelNy = 1
where stSeq = #{stSeq}
