mapper.sql:
			FROM
		    team t
		LEFT JOIN division d ON t.division_dvSeq = d.dvSeq
		LEFT JOIN league l ON t.league_lgSeq = l.lgSeq
		LEFT JOIN venue v ON t.venue_stSeq = v.stSeq
		WHERE
		    1=1

		AND tmActive = #{shUseNy}
        AND tmDelNy = #{shDelNy}


				AND tmRegTime BETWEEN #{shDateStart} AND #{shDateEnd}
				AND tmModTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND tmSeq = #{shValue}
            AND tmHomeName LIKE CONCAT('%',#{shValue},'%')
            AND tmName LIKE CONCAT('%',#{shValue},'%')
            AND tmHomeNameKor LIKE CONCAT('%',#{shValue},'%')
            AND tmNameKor LIKE CONCAT('%',#{shValue},'%')
            AND tmNameAb LIKE CONCAT('%',#{shValue},'%')


			AND tmLeague =#{shLeagueOption}
			AND tmDiv =#{shDivOption}

mapper.insert:
INSERT INTO team ( tmSeq, tmName, tmClubName, tmAbbreviation, tmLocation, tmCode, tmFileCode, tmStPlay, tmActive, tmDelNy, tmLogoUrl, tmRegTime, tmModTime, division_dvSeq, league_lgSeq, venue_stSeq ) values ( #{tmSeq}, #{tmName}, #{tmClubName}, #{tmAbbreviation}, #{tmLocation}, #{tmCode}, #{tmFileCode}, #{tmStPlay}, 1, 0, #{tmLogoUrl}, #{tmRegTime}, #{tmModTime}, #{division_dvSeq}, #{league_lgSeq}, #{venue_stSeq}
)
SELECT last_insert_id()

mapper.delete:
Update team set
	tmDelNy = 1
where tmSeq = #{tmSeq}
