mapper.select:
SELECT COUNT(*) 
FROM user 
WHERE urID = #{urID}

SELECT COUNT(*) 
FROM user 
WHERE urNickname = #{urNickname}

mapper.update:
update user set
	urID = #{urID}
where
	urSeq = #{urSeq}

update user set
	urPassword = #{urPassword}
where
	urSeq = #{urSeq}

update user set
	emailID = #{emailID}
	email = #{email}
where
	urSeq = #{urSeq}

update user set
	telecom = #{telecom}
	phoneNumber = #{phoneNumber}
where
	urSeq = #{urSeq}

